            'Level_2': ['AE', 'BR', 'IN', 'ZA', 'MX'],
            'Level_3': ['IR', 'KP', 'SY', 'RU', 'CU'],
            'scores': {'Level_1': 2, 'Level_2': 4, 'Level_3': 10}
